import { FILTER_BY_TYPE, GET_POKEMONS, GET_POKEMON_ID, GET_POKEMON_NAME } from "./action_types";

const initialState = {
  pokemons: [],
  pokemonDetail: {},
  pokemonByName: [],
  actualPage: 1,
  pokemonPerPage: 12,
  filteredPokemons: [],
  filteredType: "allPokemons"
}

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case GET_POKEMONS:
      return { ...state, pokemons: action.payload }
    case GET_POKEMON_ID:
      return { ...state, pokemonDetail: action.payload }
    case GET_POKEMON_NAME:
      return { ...state, pokemonByName: action.payload }
    case FILTER_BY_TYPE:
      const { pokemons, filteredType } = state;
      const filteredByType =
        filteredType === "allPokemons"
          ? pokemons
          : pokemons?.filter((pokemon) =>
              pokemon.types.some((type) => type.name === filteredType)
            );

      return {
        ...state,
        filteredType,
        pokemons: filteredByType,
      };
    default:
      return state;
  }
}

export default reducer;
