const Pagination = ({ actualPage, pokemonPerPage, totalPokemon, onPageChange }) => {
    const totalPages = Math.ceil(totalPokemon / pokemonPerPage)

    const pageNum = Array.from({ length: totalPages }, (_, index) => index + 1);

    return(
        <nav>
            <ul>
                {pageNum?.map(pageNum => (
                    <li 
                    key={pageNum}
                    className={`page-item ${pageNum === actualPage ? "active" : ""}`}>
                        <button onClick={() => onPageChange(pageNum)}>
                            {pageNum}
                        </button>
                    </li>
                ))}
            </ul>
        </nav>
    )
}

export default Pagination;
