import { useState } from "react";

export const SearchBar = ({ onSearch }) => {
    const [search, setSearch] = useState("");
    const [error, setError] = useState("");
  
    const handleChange = (event) => {
      setSearch(event.target.value);
      setError(""); // Restablecer el mensaje de error al cambiar el valor de búsqueda
    };
  
    const handleSubmit = (event) => {
      event.preventDefault();
      if (search.trim() === "") {
        setError("Ingrese un nombre de Pokémon válido");
        return;
      }
      onSearch(search);
      setSearch(""); // Restablecer el campo de búsqueda después de la búsqueda
    };
  
    return (
      <form onSubmit={handleSubmit}>
        <input type="search" onChange={handleChange} value={search} placeholder="Inserte el nombre del Pokémon" />
        <button type="submit">Buscar</button>
        {error && <p>{error}</p>} {/* Mostrar el mensaje de error */}
      </form>
    );
  };
