import axios from 'axios';
import { FILTER_BY_ORIGIN, FILTER_BY_TYPE, GET_POKEMONS, GET_POKEMON_ID, GET_POKEMON_NAME, ORDER_ABC, ORDER_ATTACK } from "./action_types";

// export const getPokemons = (page) => {
//     return async function(dispatch){
//         const apiData = await axios.get(`http://localhost:3001/pokemons?page=${page}`);
//         const pokemons = apiData.data;
//         dispatch({ type: GET_POKEMONS, payload: pokemons });
//     }
// }

export const getPokemonById = (id) => {
    return async function(dispatch){
        const apiData = await axios.get(`http://localhost:3001/pokemons/${id}`);
        const pokemonDetail = apiData.data;
        dispatch({ type: GET_POKEMON_ID, payload: pokemonDetail });
    }
}

export const getPokemonByName = (name) => {
    return async function(dispatch){
      const apiData = await axios.get(`http://localhost:3001/pokemons/name?name=${name}`);
      const pokemonByName = [apiData.data];
      dispatch({ type: GET_POKEMON_NAME, payload: pokemonByName });
    }
  };

export const filterByType = (type) => {
    return({
        type: FILTER_BY_TYPE,
        payload: type
    })
}

export const filterByOrigin = (origin) => {
    const isCreated = origin === "true";

    if (origin === 'allPokemons') {
      return getPokemons()
    } else {
      return {
        type: FILTER_BY_ORIGIN,
        payload: isCreated, 
      };
    }
  };

export const orderByABC = (orderABC) => {
    return({
        type: ORDER_ABC,
        payload: orderABC
    })
}

export const getPokemonsWithOrder = (page, order) => {
  return async function (dispatch) {
    const apiData = await axios.get(
      `http://localhost:3001/pokemons?page=${page}`
    );
    const pokemons = apiData.data;

    if (order) {
      const orderedPokemons =
        order === "A"
          ? pokemons.sort((a, b) => a.name.localeCompare(b.name))
          : pokemons.sort((a, b) => b.name.localeCompare(a.name));
      dispatch({ type: GET_POKEMONS, payload: orderedPokemons });
    } else {
      dispatch({ type: GET_POKEMONS, payload: pokemons });
    }
  };
};

export const orderByAttack = (orderAttack) => {
    return({
        type: ORDER_ATTACK,
        payload: orderAttack
    })
}
