import { FILTER_BY_TYPE, GET_POKEMONS, GET_POKEMON_ID, GET_POKEMON_NAME } from "./action_types";

const initialState = {
  pokemons: [],
  pokemonDetail: {},
  pokemonByName: [],
  actualPage: 1,
  pokemonPerPage: 12,
  filteredType: "allPokemons"
}

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case GET_POKEMONS:
      return { ...state, pokemons: action.payload }
    case GET_POKEMON_ID:
      return { ...state, pokemonDetail: action.payload }
    case GET_POKEMON_NAME:
      return { ...state, pokemonByName: action.payload }
    case FILTER_BY_TYPE:
      const filteredByType =
        action.payload === "allPokemons"
          ? state.pokemons
          : state.pokemons.filter((pokemon) =>
              pokemon.types.some((type) => type.name === action.payload)
            );

      return {
        ...state,
        filteredType: action.payload,
        pokemons: filteredByType,
      };
    default:
      return state;
  }
}

export default reducer;
