import { FILTER_BY_ORIGIN, FILTER_BY_TYPE, GET_POKEMONS, GET_POKEMON_ID, GET_POKEMON_NAME } from "./action_types";

const initialState = {
  allPokemons: [],
  userCreatedPokemons: [],
  pokemons: [],
  pokemonDetail: {},
  pokemonByName: [],
  actualPage: 1,
  pokemonPerPage: 12,
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case GET_POKEMONS:
      return {
        ...state,
        allPokemons: action.payload,
        userCreatedPokemons: action.payload.filter(
          (pokemon) => pokemon.created
        ),
        pokemons: action.payload,
      };
    case GET_POKEMON_ID:
      return { ...state, pokemonDetail: action.payload };
    case GET_POKEMON_NAME:
      return { ...state, pokemonByName: action.payload };
    case FILTER_BY_TYPE:
      const filteredType = state.allPokemons.filter(pokemon =>
          pokemon.types.some(type => type.name === action.payload)
      );
      
      return {
          ...state,
          pokemons:
            action.payload === "allPokemons" ? state.allPokemons : filteredType,
      };
      case FILTER_BY_ORIGIN:
      if (action.payload === "allPokemons") {
        return {
          ...state,
          pokemons: [...state.allPokemons, ...state.userCreatedPokemons],
        };
      } else {
        const filteredOrigin = state.allPokemons.filter(
          (pokemon) => pokemon.created === action.payload
        );
        return {
          ...state,
          pokemons: filteredOrigin,
        };
      }
    default:
      return state;
  }
};

export default reducer;
