import { GET_POKEMONS, GET_POKEMON_ID, GET_POKEMON_NAME } from "./action_types";

const initialState = {
    pokemons: [],
    pokemonDetail: {},
    pokemonByName: { results: [] }
}

const reducer = (state=initialState, action) => {
    switch (action.type) {
        case GET_POKEMONS:
            return { ...state, pokemons: action.payload }
        case GET_POKEMON_ID:
            return { ...state, pokemonDetail: action.payload }
        case GET_POKEMON_NAME:
            return { ...state, pokemonByName: { result: action.payload } }
        default:
            return {...state};
    }
}

export default reducer;
