import CardsContainer from "../../components/CardsContainer/CardsContainer";
import SearchBar from "../../components/SearchBar/SearchBar";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getPokemons, getPokemonByName } from '../../redux/actions'

const Home = () => {
    const dispatch = useDispatch();
    const [search, setSearch] = useState("");
    const pokemons = useSelector(state => state.pokemons);
    const pokemonByName = useSelector(state => state.pokemonByName);
    console.log(pokemonByName);
    useEffect(() => {
        dispatch(getPokemons());
    }, [dispatch]);

    const handleSearch = (search) => {
        setSearch(search);
        dispatch(getPokemonByName(search));
    };

    const filteredPokemons = pokemons?.filter(pokemon =>
        pokemon.name.toLowerCase().includes(search.toLowerCase())
    );

    const filteredPokemonsName = pokemonByName.length > 0 ? pokemonByName.filter(pokemon =>
        pokemon.name.toLowerCase().includes(search.toLowerCase())
    ) : [];
    
    const displayedPokemon = filteredPokemons.length > 0 ? filteredPokemons : filteredPokemonsName;

    const showPokemon = displayedPokemon.length > 0 ? displayedPokemon : [];
    
    return (
      <div>
        <SearchBar onSearch={handleSearch} />
        <CardsContainer pokemons={showPokemon} />
      </div>
    );
}

export default Home;
