import { FILTER_BY_ORIGIN, FILTER_BY_TYPE, GET_POKEMONS, GET_POKEMON_ID, GET_POKEMON_NAME, ORDER_ABC } from "./action_types";

const initialState = {
  allPokemons: [],
  pokemons: [],
  pokemonDetail: {},
  pokemonByName: [],
  actualPage: 1,
  pokemonPerPage: 12,
  currentOrder: null
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case GET_POKEMONS:
      return {
        ...state,
        allPokemons: action.payload,
        pokemons: action.payload,
      };
    case GET_POKEMON_ID:
      return { ...state, pokemonDetail: action.payload };
    case GET_POKEMON_NAME:
      return { ...state, pokemonByName: action.payload };
    case FILTER_BY_TYPE:
      const filteredType = state.allPokemons.filter(pokemon =>
          pokemon.types.some(type => type.name === action.payload)
      );
      
      return {
          ...state,
          pokemons:
            action.payload === "allPokemons" ? state.allPokemons : filteredType,
      };
      case FILTER_BY_ORIGIN:
        const filteredOrigin = state.allPokemons.filter(
          (pokemon) => pokemon.created === action.payload
        );
  
        return {
          ...state,
          pokemons:
            action.payload === "allPokemons" ? state.allPokemons : filteredOrigin,
        };
        case ORDER_ABC:
          const orderedPokemons =
            action.payload === "A"
              ? state.allPokemons.slice().sort((a, b) => a.name.localeCompare(b.name))
              : state.allPokemons.slice().sort((a, b) => b.name.localeCompare(a.name));
        
          return {
            ...state,
            pokemons: orderedPokemons,
            currentOrder: action.payload,
          };
        

        

      default: 
        return state;
  };
}

export default reducer;
