import { FILTER_BY_ORIGIN, FILTER_BY_TYPE, GET_POKEMONS, GET_POKEMON_ID, GET_POKEMON_NAME } from "./action_types";

const initialState = {
  allPokemons: [],
  filteredPokemons: [], // Nueva propiedad para almacenar los Pokémon filtrados
  pokemons: [],
  pokemonDetail: {},
  pokemonByName: [],
  actualPage: 1,
  pokemonPerPage: 12,
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case GET_POKEMONS:
      return {
        ...state,
        allPokemons: action.payload,
        filteredPokemons: action.payload,
        pokemons: action.payload,
      };
    case GET_POKEMON_ID:
      return { ...state, pokemonDetail: action.payload };
    case GET_POKEMON_NAME:
      return { ...state, pokemonByName: action.payload };
    case FILTER_BY_TYPE:
      const filteredType = state.allPokemons.filter(pokemon =>
        pokemon.types.some(type => type.name === action.payload)
      );
      return {
        ...state,
        filteredPokemons: filteredType,
        pokemons:
          action.payload === "allPokemons" ? state.filteredPokemons : filteredType,
      };
    case FILTER_BY_ORIGIN:
      if (action.payload === "allPokemons") {
        return {
          ...state,
          pokemons: state.filteredPokemons,
        };
      } else {
        const isCreated = action.payload === "true";
        const filteredOrigin = state.allPokemons.filter(
          pokemon => Boolean(pokemon.created) === isCreated
        );
        return {
          ...state,
          filteredPokemons: filteredOrigin,
          pokemons: filteredOrigin,
        };
      }
    default:
      return state;
  }
};
